import React, { useState } from "react";
import '../estilos/RecuperacionContrasenia';

function RecuperacionContrasenia() {
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleConfirmPasswordChange = (e) => {
    setConfirmPassword(e.target.value);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setError("Las contraseñas no coinciden");
    } else {
      setError("");
      // Handle the password update logic here
      alert("Contraseña actualizada con éxito");
    }
  };

  return (
    <div className="recuperacionContra">
      <form onSubmit={handleSubmit}>
        <label htmlFor="password">Nueva Contraseña</label>
        <div className="password-input">
          <input
            type={showPassword ? "text" : "password"}
            id="password"
            value={password}
            onChange={handlePasswordChange}
            className="inputField"
            required
          />
          <button
            type="button"
            onClick={togglePasswordVisibility}
            className="toggleButton"
          >
            {showPassword ? "Ocultar" : "Mostrar"}
          </button>
        </div>
        <label htmlFor="confirmPassword">Confirmación de la Contraseña</label>
        <div className="password-input">
          <input
            type={showPassword ? "text" : "password"}
            id="confirmPassword"
            value={confirmPassword}
            onChange={handleConfirmPasswordChange}
            className="inputField"
            required
          />
        </div>
        {error && <p className="errorMessage">{error}</p>}
        <button type="submit" className="submitButton">Actualizar Contraseña</button>
      </form>
    </div>
  );
}

export default RecuperacionContrasenia;
