import React from 'react';
import '../estilos/ProductCard.css';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import { Link } from 'react-router-dom';




const ProductCard = ({ producto }) => {
  return (
    <div className="product-card">
      <img src={producto.imgSrc} alt={producto.nombre} className="product-image" />
      <div className="product-info">
        <h3 className="product-name">{producto.nombre}</h3>
        <p className="product-price">
          {producto.precio} <span className="old-price">{producto.precioAntiguo}</span>
        </p>
        <div className="product-actions">
          <Link to="/favoritos">  
            <FavoriteIcon className="action-icon" />
          </Link>
          <Link to="/carrito">  
            <ShoppingCartIcon className="action-icon" />
          </Link>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
